import { BigNumberLike } from "@rarible/types";
import { Creator } from "./Creator";
import { ItemId } from "@rarible/types";
import { Royalty } from "./Royalty";
export type LazyItem = EthLazyItem;
export type EthLazyItem = EthLazyItemErc721 | EthLazyItemErc1155;
export type EthLazyItemErc721 = {
    "@type": "ETH_ERC721";
    id: ItemId;
    uri: string;
    creators: Array<Creator>;
    royalties: Array<Royalty>;
    signatures: Array<string>;
};
export type EthLazyItemErc1155 = {
    "@type": "ETH_ERC1155";
    id: ItemId;
    uri: string;
    creators: Array<Creator>;
    royalties: Array<Royalty>;
    signatures: Array<string>;
    supply: BigNumberLike;
};
