import { CollectionId } from "@rarible/types";
import { CurrencyId } from "@rarible/types";
import { EventTimeMarks } from "./EventTimeMarks";
import { OlapVolumeForPeriodData } from "./OlapVolumeForPeriodData";
export type OlapCollectionEvent = OlapCollectionVolumeChangeEvent | OlapCollectionFloorPriceByCurrencyChangeEvent | OlapCollectionTopPriceByCurrencyChangeEvent;
export type OlapCollectionVolumeChangeEvent = {
    "@type": "VOLUME_CHANGE";
    collectionId: CollectionId;
    eventId: string;
    eventTimeMarks?: EventTimeMarks;
    data: Array<OlapVolumeForPeriodData>;
};
export type OlapCollectionFloorPriceByCurrencyChangeEvent = {
    "@type": "FLOOR_PRICE_BY_CURRENCY_CHANGE";
    collectionId: CollectionId;
    eventId: string;
    eventTimeMarks?: EventTimeMarks;
    orderId?: string;
    price?: string;
    currencyId: CurrencyId;
    updatedAt: string;
};
export type OlapCollectionTopPriceByCurrencyChangeEvent = {
    "@type": "TOP_PRICE_BY_CURRENCY_CHANGE";
    collectionId: CollectionId;
    eventId: string;
    eventTimeMarks?: EventTimeMarks;
    price?: string;
    currencyId: CurrencyId;
    updatedAt: string;
};
