import { CollectionId } from "@rarible/types";
import { EventTimeMarks } from "./EventTimeMarks";
import { OlapCollectionStatsFloorChangeData } from "./OlapCollectionStatsFloorChangeData";
import { OlapCollectionStatsTopOfferChangeData } from "./OlapCollectionStatsTopOfferChangeData";
import { OlapCollectionStatsVolumeChangeData } from "./OlapCollectionStatsVolumeChangeData";
export type OlapCollectionStatsEvent = OlapCollectionStatsVolumeChangeEvent | OlapCollectionStatsFloorChangeEvent | OlapCollectionStatsTopOfferChangeEvent;
export type OlapCollectionStatsVolumeChangeEvent = {
    "@type": "VOLUME_CHANGE";
    eventId: string;
    collectionId: CollectionId;
    eventTimeMarks?: EventTimeMarks;
    data: OlapCollectionStatsVolumeChangeData;
};
export type OlapCollectionStatsFloorChangeEvent = {
    "@type": "FLOOR_CHANGE";
    eventId: string;
    collectionId: CollectionId;
    eventTimeMarks?: EventTimeMarks;
    data: OlapCollectionStatsFloorChangeData;
};
export type OlapCollectionStatsTopOfferChangeEvent = {
    "@type": "TOP_OFFER_CHANGE";
    eventId: string;
    collectionId: CollectionId;
    eventTimeMarks?: EventTimeMarks;
    data: OlapCollectionStatsTopOfferChangeData;
};
