import { BigNumberLike } from "@rarible/types";
import { Blockchain } from "./Blockchain";
export type SignatureInputForm = OpenSeaFillOrderSignatureInputForm | X2Y2FillOrderSignatureInputForm | X2Y2CancelOrderSignatureInputForm;
export type OpenSeaFillOrderSignatureInputForm = {
    "@type": "OPEN_SEA_ORDER_FILL";
    blockchain: Blockchain;
    signature?: string;
};
export type X2Y2FillOrderSignatureInputForm = {
    "@type": "X2Y2_ORDER_FILL";
    blockchain: Blockchain;
    caller: string;
    op: BigNumberLike;
    orderId: BigNumberLike;
    currency: string;
    price: BigNumberLike;
    tokenId?: BigNumberLike;
};
export type X2Y2CancelOrderSignatureInputForm = {
    "@type": "X2Y2_ORDER_CANCEL";
    blockchain: Blockchain;
    caller: string;
    op: BigNumberLike;
    orderId: BigNumberLike;
    signMessage: string;
    sign: string;
};
