import { ActivityFilterAllType } from "./ActivityFilterAllType";
import { ActivityFilterByCollectionType } from "./ActivityFilterByCollectionType";
import { ActivityFilterByItemType } from "./ActivityFilterByItemType";
import { ActivityFilterByUserType } from "./ActivityFilterByUserType";
import { BigNumberLike } from "@rarible/types";
import { EVMAddress } from "@rarible/types";
export type ActivityFilter = ActivityFilterAll | ActivityFilterByUser | ActivityFilterByItem | ActivityFilterByCollection;
export type ActivityFilterAll = {
    "@type": "all";
    types: Array<ActivityFilterAllType>;
};
export type ActivityFilterByUser = {
    "@type": "by_user";
    users: Array<EVMAddress>;
    types: Array<ActivityFilterByUserType>;
    from?: number;
    to?: number;
};
export type ActivityFilterByItem = {
    "@type": "by_item";
    contract: EVMAddress;
    tokenId: BigNumberLike;
    types: Array<ActivityFilterByItemType>;
};
export type ActivityFilterByCollection = {
    "@type": "by_collection";
    contract: EVMAddress;
    types: Array<ActivityFilterByCollectionType>;
};
