import { BigNumberLike } from "@rarible/types";
import { Binary } from "@rarible/types";
import { EVMAddress } from "@rarible/types";
import { Part } from "./Part";
export type AssetType = EthAssetType | Erc20AssetType | Erc721AssetType | Erc1155AssetType | Erc721LazyAssetType | Erc1155LazyAssetType | CryptoPunksAssetType | CollectionAssetType | AmmNftAssetType | GenerativeArtAssetType;
export type EthAssetType = {
    assetClass: "ETH";
};
export type Erc20AssetType = {
    assetClass: "ERC20";
    contract: EVMAddress;
};
export type Erc721AssetType = {
    assetClass: "ERC721";
    contract: EVMAddress;
    tokenId: BigNumberLike;
};
export type Erc1155AssetType = {
    assetClass: "ERC1155";
    contract: EVMAddress;
    tokenId: BigNumberLike;
};
export type Erc721LazyAssetType = {
    assetClass: "ERC721_LAZY";
    contract: EVMAddress;
    tokenId: BigNumberLike;
    uri: string;
    creators: Array<Part>;
    royalties: Array<Part>;
    signatures: Array<Binary>;
};
export type Erc1155LazyAssetType = {
    assetClass: "ERC1155_LAZY";
    contract: EVMAddress;
    tokenId: BigNumberLike;
    uri: string;
    supply: BigNumberLike;
    creators: Array<Part>;
    royalties: Array<Part>;
    signatures: Array<Binary>;
};
export type CryptoPunksAssetType = {
    assetClass: "CRYPTO_PUNKS";
    contract: EVMAddress;
    tokenId: number;
};
export type CollectionAssetType = {
    assetClass: "COLLECTION";
    contract: EVMAddress;
};
export type AmmNftAssetType = {
    assetClass: "AMM_NFT";
    contract: EVMAddress;
};
export type GenerativeArtAssetType = {
    assetClass: "GEN_ART";
    contract: EVMAddress;
};
