import { Erc20Allowance } from "./Erc20Allowance";
import { Erc20Balance } from "./Erc20Balance";
import { EventTimeMarks } from "./EventTimeMarks";
export type Erc20BalanceEvent = Erc20BalanceUpdateEvent | Erc20AllowanceEvent;
export type Erc20BalanceUpdateEvent = {
    type: "UPDATE";
    eventId: string;
    balanceId: string;
    createdAt?: string;
    lastUpdatedAt?: string;
    eventTimeMarks?: EventTimeMarks;
    balance: Erc20Balance;
};
export type Erc20AllowanceEvent = {
    type: "ALLOWANCE";
    eventId: string;
    balanceId: string;
    createdAt?: string;
    lastUpdatedAt?: string;
    eventTimeMarks?: EventTimeMarks;
    allowance: Erc20Allowance;
};
