import { BigNumberLike } from "@rarible/types";
import { EVMAddress } from "@rarible/types";
import { Part } from "./Part";
export type ItemHistory = ItemRoyalty | ItemTransfer;
export type ItemRoyalty = {
    type: "ROYALTY";
    owner?: EVMAddress;
    contract: EVMAddress;
    tokenId: BigNumberLike;
    value?: BigNumberLike;
    date: string;
    royalties: Array<Part>;
};
export type ItemTransfer = {
    type: "TRANSFER";
    owner: EVMAddress;
    contract: EVMAddress;
    tokenId: BigNumberLike;
    value: BigNumberLike;
    date: string;
    from: EVMAddress;
};
