import { EVMAddress } from "@rarible/types";
import { EventTimeMarks } from "./EventTimeMarks";
import { NftCollection } from "./NftCollection";
export type NftCollectionEvent = NftCollectionUpdateEvent | NftCollectionSetBaseUriEvent;
export type NftCollectionUpdateEvent = {
    type: "UPDATE";
    eventId: string;
    id: EVMAddress;
    eventTimeMarks?: EventTimeMarks;
    collection: NftCollection;
};
export type NftCollectionSetBaseUriEvent = {
    type: "SET_BASE_URI";
    eventId: string;
    id: EVMAddress;
    eventTimeMarks?: EventTimeMarks;
};
