import { EventTimeMarks } from "./EventTimeMarks";
import { NftDeletedItem } from "./NftDeletedItem";
import { NftItem } from "./NftItem";
export type NftItemEvent = NftItemUpdateEvent | NftItemDeleteEvent;
export type NftItemUpdateEvent = {
    type: "UPDATE";
    eventId: string;
    itemId: string;
    eventTimeMarks?: EventTimeMarks;
    item: NftItem;
};
export type NftItemDeleteEvent = {
    type: "DELETE";
    eventId: string;
    itemId: string;
    eventTimeMarks?: EventTimeMarks;
    item: NftDeletedItem;
};
