import { BigNumberLike } from "@rarible/types";
import { EVMAddress } from "@rarible/types";
import { Order } from "./Order";
export type NftOrdersPriceUpdateEvent = NftSellOrdersPriceUpdateEvent | NftBidOrdersPriceUpdateEvent;
export type NftSellOrdersPriceUpdateEvent = {
    "@type": "SELL_ORDERS_PRICE_UPDATE";
    eventId: string;
    contract: EVMAddress;
    tokenId: BigNumberLike;
    orders: Array<Order>;
};
export type NftBidOrdersPriceUpdateEvent = {
    "@type": "BID_ORDERS_PRICE_UPDATE";
    eventId: string;
    contract: EVMAddress;
    tokenId: BigNumberLike;
    orders: Array<Order>;
};
