import { EventTimeMarks } from "./EventTimeMarks";
import { NftDeletedOwnership } from "./NftDeletedOwnership";
import { NftOwnership } from "./NftOwnership";
export type NftOwnershipEvent = NftOwnershipUpdateEvent | NftOwnershipDeleteEvent;
export type NftOwnershipUpdateEvent = {
    type: "UPDATE";
    eventId: string;
    ownershipId: string;
    eventTimeMarks?: EventTimeMarks;
    blockNumber?: number;
    ownership: NftOwnership;
};
export type NftOwnershipDeleteEvent = {
    type: "DELETE";
    eventId: string;
    ownershipId: string;
    eventTimeMarks?: EventTimeMarks;
    blockNumber?: number;
    ownership?: NftDeletedOwnership;
    deletedOwnership?: NftOwnership;
};
