import { Asset } from "./Asset";
import { BigNumberLike } from "@rarible/types";
import { EVMAddress } from "@rarible/types";
import { OrderSide } from "./OrderSide";
import { Word } from "@rarible/types";
export type OrderExchangeHistory = OrderCancel | OrderSideMatch | OnChainOrder | OnChainAmmOrder;
export type OrderCancel = {
    type: "CANCEL";
    hash: Word;
    make?: Asset;
    take?: Asset;
    date: string;
    maker?: EVMAddress;
    owner?: EVMAddress;
};
export type OrderSideMatch = {
    type: "ORDER_SIDE_MATCH";
    hash: Word;
    make?: Asset;
    take?: Asset;
    date: string;
    maker?: EVMAddress;
    side?: OrderSide;
    fill: BigNumberLike;
    taker?: EVMAddress;
    counterHash?: Word;
    makeUsd?: BigNumberLike;
    takeUsd?: BigNumberLike;
    makePriceUsd?: BigNumberLike;
    takePriceUsd?: BigNumberLike;
};
export type OnChainOrder = {
    type: "ON_CHAIN_ORDER";
    hash: Word;
    make?: Asset;
    take?: Asset;
    date: string;
    maker?: EVMAddress;
};
export type OnChainAmmOrder = {
    type: "ON_CHAIN_AMM_ORDER";
    hash: Word;
    make?: Asset;
    take?: Asset;
    date: string;
    maker?: EVMAddress;
};
