import { toBigNumberLike } from "@rarible/types";
import { toBn } from "@rarible/utils";
import { concatNonUniqueFees } from "../order/common/calculate-sale-cuts";
export function calculateFees(price, fees) {
    var calculatedFees = fees.map(function (fee) {
        var value = toBigNumberLike(toBn(price.toString()).multipliedBy(fee.value).decimalPlaces(8).toString());
        return { account: fee.account, value: value };
    });
    return concatNonUniqueFees(calculatedFees);
}
