import { toBn } from "@rarible/utils";
import { MIN_ORDER_PRICE } from "../config/config";
import { fixAmount } from "./fix-amount";
export function checkPrice(price) {
    var fixedPrice = fixAmount(price);
    if (toBn(fixedPrice).lt(toBn(MIN_ORDER_PRICE))) {
        throw new Error("Invalid price, minimal value is ".concat(MIN_ORDER_PRICE));
    }
}
