import type { FlowAddress, FlowContractAddress } from "@rarible/types";
import type { FlowContractAddressName } from "../flow-address";
import type { NonFungibleContract } from "../../types";
export type FlowCollectionName = FlowContractAddressName & {
    __IS_FLOW_COLLECTION_NAME__: true;
};
export type FlowCollectionAddress = FlowContractAddress & {
    __IS_FLOW_KNOWN_COLLECTION_ADDRESS__: true;
};
export declare function isFlowCollection(address: FlowContractAddress): address is FlowCollectionAddress;
export type FlowCollectionData = {
    name: NonFungibleContract;
    address: FlowAddress;
};
export declare function getCollectionData(str: FlowContractAddress): FlowCollectionData;
