export function sansPrefix(address) {
    var replaced = address.replace(/^0x/, "");
    if (isSansed(replaced)) {
        return replaced;
    }
    throw new Error("Can't remove 0x from string");
}
export function isSansed(str) {
    return !str.startsWith("0x");
}
export function withPrefix(address) {
    if (isPrefixed(address)) {
        return address;
    }
    return "0x" + address;
}
function isPrefixed(str) {
    return str.startsWith("0x");
}
