import { __assign } from "tslib";
import { createFlowSdk } from "../../index";
export function createTestFlowSdk(fcl, network, params, auth) {
    return createFlowSdk(fcl, network, __assign({ apiKey: getAPIKey(network) }, params), auth);
}
export function getAPIKey(env) {
    switch (env) {
        case "mainnet":
            return process.env.SDK_API_KEY_PROD;
        default:
            return process.env.SDK_API_KEY_TESTNET;
    }
}
