import { CONFIGS } from "../../config/config";
import { replaceImportAddresses } from "../../common/template-replacer";
import { prepareFtCode } from "../../tx-code-store/order/prepare-order-code";
import { getBalanceScripts } from "../../scripts/wallet/balance";
export function getPreparedAddressArgument(address) {
    return dataToBase64String(JSON.stringify({ "type": "Address", "value": address }));
}
export function getPreparedCadenceScript(network, currency) {
    var map = CONFIGS[network].mainAddressMap;
    switch (currency) {
        case "FLOW":
        case "FUSD":
        case "USDC":
            return dataToBase64String(replaceImportAddresses(prepareFtCode(getBalanceScripts.common, currency), map));
        default:
            throw new Error("Flow-sdk Error: Unsupported currency");
    }
}
function dataToBase64String(data) {
    return Buffer.from(data).toString("base64");
}
